jQuery(document).ready(function(){
	readyInit();
	$(window).on('scroll',scrollInit);
	$(window).on('resize',resizeInit);})
	;var readyInit=function(){
		setTimeout(function(){
			detectOs();
			ifBrowserIE();
			buildMobileMenu('.menu.mobile-menu');
			addNavbarClass();navColorSwitcher();
			initAnchorLinks();
			dataFilterAction();
			toTopScrollVisibility();
			blockDropdownAction();
		},50);
		$('html').removeClass('no-js');
	};
	var resizeInit=function(){toTopScrollVisibility();};
	var scrollInit=function(){addNavbarClass();navColorSwitcher();toTopScrollVisibility();}; 
	function blockDropdownAction(){
		var drop_list=$("ul.dropdown-list"),
		user_login=$("ul.dropdown-list.user"),
		admin_login=$("ul.dropdown-list.admin");
		if(!drop_list){return false;}
		admin_login.find("li .block-content.dropdown-action").each(function(){
			var $this=$(this);
			$this.addClass("close");
			$this.parents(".content-wrapper").find(".btn-block-content-dropdown").html("+");
			$this.parents(".content-wrapper").find(".btn-block-content-dropdown").addClass("show");
			$this.parents(".caption_block").addClass("short");
		});
		user_login.find(".block-content.dropdown-action").each(function(){
			var $this=$(this);
			$this.removeClass("close");
			$this.parents(".content-wrapper").find(".btn-block-content-dropdown").html("-");
			$this.parents(".content-wrapper").find(".btn-block-content-dropdown").addClass("hide");
			$this.parents(".caption_block").removeClass("short");
		});
		$(".content-wrapper .btn-block-content-dropdown").click(function(){
			var $this=$(this);
			var drop_block=$this.parent().find(".block-content.dropdown-action");
			if(drop_block.hasClass("close")){
				$this.html("-");
				$this.addClass("hide");
				$this.removeClass("show");
				drop_block.removeClass("close");
				$this.siblings(".caption_block").removeClass("short");
			}else{
				drop_block.addClass("close");
				$this.html("+");
				$this.removeClass("hide");
				$this.addClass("show");
				$this.siblings(".caption_block").addClass("short");
			}
		});
		$(".content-wrapper .cab-heading").click(function(){
			var $this=$(this);
			var drop_block=$this.siblings(".block-content.dropdown-action");
			if(drop_block.hasClass("close")){
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").html("-");
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").addClass("hide");
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").removeClass("show");
				drop_block.removeClass("close");
				$this.parent().removeClass("short");
			}else{
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").html("+");
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").removeClass("hide");
				$this.parents(".content-wrapper").find(".btn-block-content-dropdown").addClass("show");
				drop_block.addClass("close");
				$this.parent().addClass("short");
			}
		});
	}
